package chapter2;

public class CharaterEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch1 = 'A';
		System.out.println("문자 출력 : " + ch1);
		System.out.println("문자에 해당하는 정수값 출력 : " + (int)ch1);

		char ch2 = 66;
		System.out.println("정수 값에 해당하는 문자 출력 : " + ch2);
		
		int ch3 = 67;
		System.out.println("문자 출력 : " + ch3);
		System.out.println("정수값에 해당하는 문자출력 : " + (char)ch3);

	}

}
